import './App.css';
import React, { useEffect, useState } from "react";
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import Container from '@mui/material/Container';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import SearchIcon from '@mui/icons-material/Search';



function App() {
  const cityname = "Bangkok";
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [data, setData] = useState(null)
  const [weather, setItems] = useState([]);
  const handleOnclick = async () => {
    document.getElementById('ctn').innerHTML = document.getElementById('citynameTextF').value;
    var weatherLocation = document.getElementById('citynameTextF').value;
    const data = await (await fetch('http://api.openweathermap.org/data/2.5/weather?q='+weatherLocation+'&appid=fc954186a6fb9cca05088ae246960f0e&units=metric')).json()
    setData(data)
    console.log(data);
    document.getElementById('temp').innerHTML = Math.round(data["main"]["temp"]);
  useEffect(() => {
    fetch('http://api.openweathermap.org/data/2.5/weather?q=Bangkok&appid=fc954186a6fb9cca05088ae246960f0e&units=metric')
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])
  console.log(weather["main"]);
  return (
    <div className="App" >
     <br></br>
     <Box display="flex" justifyContent = "center" alignItems="center"  maxWidth="100">
      <TextField 
        id="citynameTextF" 
        variant="outlined" 
        size="small"  
      />
      &nbsp;&nbsp;
      <Button  id="search"  variant="outlined" type="submit" onClick = {handleOnclick} startIcon={<SearchIcon/>}>Search</Button>

     </Box>
      <br></br><br></br>
      <Container sx = {{width: 275}} fixed >
        <Card sx={{ minWidth: 275 , width: 275 , borderRadius: '16px'}}>
        <CardContent>
          <Typography id = 'ctn' sx={{ fontSize: 20}} color="text.primary" gutterBottom>
            {cityname}
          </Typography>
          <Typography variant="h5" component="div">
            <img src='https://cdn-icons-png.flaticon.com/512/1163/1163662.png'
            height="100px"
            width="100px"/>
          </Typography>
          <Typography id="temp" variant="h5" component="div">
            {Math.round(weather["main"]["temp"])} °C
          </Typography>
          <Typography sx={{ mb: 1.5 }} color="text.secondary">
            Clear
          </Typography>
        </CardContent>
      </Card>
      </Container>

    </div>
  );
}
}

export default App;
