import './App.css';
import React, { useEffect, useState } from "react";
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import Container from '@mui/material/Container';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import SearchIcon from '@mui/icons-material/Search';
import { alpha } from '@mui/material';
import { bgcolor } from '@mui/system';
import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';
import { styled } from '@mui/material/styles';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
  borderRadius: '16px'
}));

function App() {
  const cityname = "Bangkok";
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [data, setData] = useState(null)
  const [forecast, setForcast] = useState(null);
  var today = new Date();
  var dayth = String(today.getDate()).padStart(2, '0');
  var mon = String(today.getMonth()+1);
  const handleOnclick = async () => {
    document.getElementById('ctn').innerHTML = document.getElementById('citynameTextF').value;
    var weatherLocation = document.getElementById('citynameTextF').value;
    const data = await (await fetch('http://api.openweathermap.org/data/2.5/weather?q='+weatherLocation+'&appid=fc954186a6fb9cca05088ae246960f0e&units=metric')).json()
    setData(data)
    console.log(data["dt"]);
    const forcast = await (await fetch('https://api.open-meteo.com/v1/forecast?latitude='+data["coord"]["lat"]+'&longitude='+data["coord"]["lon"]+'&daily=temperature_2m_max,temperature_2m_min,weathercode&timezone=Asia%2FBangkok')).json()
    setForcast(forcast)
    console.log(forcast["daily"]["time"]);
    document.getElementById('temp').innerHTML = Math.round(data["main"]["temp"])+" Â°C";
    document.getElementById('weatherDetail').innerHTML = data["weather"][0]["main"];
    document.getElementById('conditionImg').src = "https://openweathermap.org/img/wn/"+data["weather"][0]["icon"]+"@2x.png"
    const milliseconds = data["dt"] * 1000 
    const dateObject = new Date(milliseconds)
    const datetime = dateObject.toLocaleString() 
    var daythNum = Number(dayth)
    var monNum = Number(mon)
    document.getElementById('date1').innerHTML = daythNum+"/"+monNum;
    document.getElementById('date2').innerHTML = daythNum+"/"+monNum;
  }
  return (
    <div className="App" >
     <br></br>
     <Box display="flex" justifyContent = "center" alignItems="center"  maxWidth="100">
      <TextField 
        id="citynameTextF" 
        variant="outlined" 
        size="small"  
      />
      &nbsp;&nbsp;
      <Button  id="search"  variant="outlined" type="submit" onClick = {handleOnclick} startIcon={<SearchIcon/>}>Search</Button>

     </Box>
      <br></br><br></br>
      <Container fixed >
      <Stack direction="row" spacing={2}>
        <Item>
          <Container sx = {{width: 260}} fixed >
            <Typography variant="h5" id = 'ctn' color="text.primary" gutterBottom>
              {cityname}
            </Typography>
            <Typography variant="h5" component="div">
              <img id="conditionImg" src='https://cdn-icons-png.flaticon.com/512/1163/1163662.png'
              height="100px"
              width="100px"/>
            </Typography>
            <Typography id='temp' variant="h5" component="div">
              -- Â°C
            </Typography>
            <Typography id="weatherDetail" sx={{ mb: 1.5 }} color="text.secondary">
              Clear
            </Typography>
            <Typography id="DateTime" hidden={true} variant="caption" sx={{ mb: 1.5 }} color="text.secondary">
            </Typography>
          </Container>
        </Item>
        <Item>
          <Container sx = {{width: 500}} fixed >
            <Typography variant='h5'>
              7 Days Forecast
            </Typography>
            <Stack direction="row" spacing={2}>
            <Typography display='flex' id='date1'/>
            <Typography display='flex' id='date2'/>
            </Stack>
          </Container>
        </Item>
      </Stack>
      </Container>


    </div>
  );
}

export default App;
